import React from "react";
import { Link } from "react-router-dom";
import "./ProjectIndexItem.css"


const ProjectIndexItem = (project) => {

    const work = project.projectData;
    const title = work.title;
    const imgSrc = work.img;
    const altTxt = work.alt;
    const tags = work.tags;
    const bio = work.bio;
    const liveSrc = work.live;
    const codeSrc = work.code;

    return(
        <>
        <div className="project-container">
            <div className="project-container-left">
                <h1 className="left-title pj-title">{title}</h1>
                {tags.map(tag => {
                    return <button disabled  className="tag" />
                })}
                {/* <button disabled className="tag">React</button>  <button disabled className="tag">Ruby on Rails</button> <button disabled className="tag">JavaScript</button>  <button disabled className="tag">Ruby</button> <button disabled className="tag">Redux</button> <button disabled className="tag">HTML</button> <button disabled className="tag">CSS</button> */}

                <p className="project-tags"> {tags} </p>
                <p className="project-description">{bio}                </p>
                <br />
                <div className="links-container">
                    <Link to={{ pathname: {codeSrc} }} target="_blank" className="pj-links">Code</Link> <Link to={{ pathname: {liveSrc} }} target="_blank" className="pj-links">Demo</Link>
                </div>
            </div>
            <div className="project-container-right">
                <img src={imgSrc} alt={altTxt} className="comp-gifs right-img"/>
            </div>

        </div>

        </>
    )
}

export default ProjectIndexItem
